import React from 'react';
import './css/Login.css'

class Login extends React.Component {
    redirect = (e) => {
        if (e.target.innerHTML === 'Log In')
            this.props.history.push('/login');
        else if (e.target.innerHTML === 'Log In') {
            this.props.history.push('/signup');
        }
    }

    render() {
        return (
            <div className="w-100 h-100 d-flex flex-column justify-content-center align-items-center">
                <div id="warning" className="w-50"></div>
                <form className="d-flex flex-column w-50 mt-2 mb-4" onSubmit={this.formHandler} name="signup-form">
                    <input type="text" className="form-control mb-2" placeholder="Username" name="username"></input>
                    <input type="text" className="form-control mb-3" placeholder="Password" name="password"></input>
                    <button type="submit" className="btn btn-outline-primary w-25 align-self-center">Login</button>
                </form>
                <div class="alert alert-primary" role="alert">
                    New user? Sign up <a href="/signup" class="alert-link">here</a>.
                </div>
            </div>
        );
    }
}



export default Login;
