import React from 'react';
import './css/Beer.scss';
import $ from 'jquery';

class Beer extends React.Component {
  trimDescription = () => {
    let description = this.props.attributes.description.slice(0, 200);
    if (description.charAt(description.length - 1) === ' ') {
      description = description.slice(0, description.length - 1);
    }
    if (description.length === this.props.attributes.description.length) {
      return description;
    }
    else {
      return description + '...';
    }
  }

  handler1 = () => {
    $(this).toggleClass('active');
  }

  handler2 = () => {
    $(this).toggleClass('is_animating');
  }

  handler3 = () => {
    $(this).toggleClass('is_animating');
  }

  render() {
    return (<div className="beer-card border border-secondary m-2">
              <div className="favorite-container">
                <div className="flexbox">
                  <div className="fav-btn">
                    <span href="" className="favme dashicons dashicons-heart" onClick={this.handler1} onTouchStart={this.handler2} onAnimationEnd={this.handler3}></span>
                  </div>
                </div>
              </div>
              <div className="img-and-description-container">
                <div className="beer-img-container">
                    <img className="beer-img" src={this.props.attributes.img} alt="Beer"></img>
                </div>
                <div className="description-container">
                  <h5 className="beer-name">{this.props.attributes.name}</h5>
                  <div className="beer-description">{this.trimDescription()}</div>
                </div>
              </div>
            </div>);
  }
}

export default Beer;
