import React from 'react';
import ReactDOM from 'react-dom';
import './css/App.css';
import Navbar from './Navbar';
import Beers from './Beers';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {beersJson: null, 
                  jsonIndex: 0,
                  searchTerm: null,
                  offset: 0,
                  clearBeers: false};
  }

  getAndDisplayBeers(searchTerm) {
    fetch(`https://api.punkapi.com/v2/beers?page=1&per_page=80&beer_name=${searchTerm}`)
    .then((response) => {
      return response.json();
    })
    .then((json) => {
      this.setState({beersJson: json,
                    searchTerm: searchTerm,
                    clearBeers: true,
                    page: 1});
      this.displayBeers();
    })
    .catch((error) => {
      console.error(error);
    })
  }

  displayBeers = () => {
    ReactDOM.render(<BeersContainer json={this.state.json}  index={this.state.jsonIndex} update={this.setState} getAndDisplayBeers={this.getAndDisplayBeers}></BeersContainer>)
  }

  render() { 
    return (
      <div>
        <Navbar getAndDisplayBeers={this.getAndDisplayBeers}></Navbar>
      </div>
    );
  }
}

export default App;
