import React from 'react';
import './css/Navbar.css';

class Navbar extends React.Component {
  constructor(props) {
    super(props);
  }

  getAndDisplayBeers = () => {
    let searchValue = document.forms['search-form']['search'].value;
    document.forms['search-form']['search'].value = null;
    this.props.getAndDisplayBeers(searchValue);
  }

  submit = (e) => {
    e.preventDefault();
    this.getAndDisplayBeers();
  }

  redirect = (e) => {
    if (e.target.id === 'logo-container') {
      this.props.resetAndRedirect('');
    }
    else if (e.target.id === 'favorites-btn') {
      this.props.resetAndRedirect('favorites');
    }
  }

  render() {
    return (
      <nav className="navbar navbar-light bg-light
      justify-content-between align-items-center bg-secondary fixed-top flex-wrap border border-secondary">
        <h1 id="logo-container" onClick={this.redirect}>Beer</h1>
        <form className="d-flex flex-grow-1" name="search-form" id="search-form" onSubmit={this.submit}>
          <input id="search" type="text" className="form-control rounded-left" placeholder="Search for beer..." name="search"></input>
          <button className="btn btn-primary rounded-right" type="submit" id="search-btn">Search</button>
        </form>
        <div class="dropdown">
          <button class="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
            Dropdown button
          </button>
          <div class="dropdown-menu" aria-labelledby="Sign in">
            <a class="dropdown-item" href="#">Action</a>
            <a class="dropdown-item" href="#">Another action</a>
            <a class="dropdown-item" href="#">Something else here</a>
          </div>
        </div>
        <button type="button" className="btn btn-primary" onClick={this.redirect} id="favorites-btn">Favorites <span className="badge badge-light">{this.props.favoriteNum}</span>
        </button>
      </nav>
    ); 
  }
}

export default Navbar;
