import React from 'react';
import './css/Favorites.css';
import Beer from './Beer';

class Favorites extends React.Component {
    favoriteContainer = () => {
        if (this.props.favoriteList !== null) {
          if (this.props.favoriteList.length < 3) {
            let html = (<div className="d-flex justify-content-center w-100 less-than-three-container">
                          {this.props.favoriteList.map(beer => <Beer key={beer.id} beerKey={beer.id} attributes={beer} removeFromfavorite={this.props.removeFromfavorite}></Beer>)}
                        </div>)
            return html;
          }
          else {
            let favoriteLists = this.createAndRenderFavoriteLists();
            let html = (<div className="d-flex justify-content-center flex-wrap w-100 greater-than-three-container">
                          <div id="beer-column-1" className="beer-column mr-2">{favoriteLists[0]}</div>
                          <div id="beer-column-2" className="beer-column">{favoriteLists[1]}</div>
                          <div id="beer-column-3" className="beer-column ml-2">{favoriteLists[2]}</div>
                        </div>);
            return html;
          }
        }
      }


    createAndRenderFavoriteLists = () => {
        let favoriteArr = this.props.favoriteList.map(beer => <Beer key={beer.id} beerKey={beer.id} attributes={beer} removeFromfavorite={this.props.removeFromfavorite}></Beer>);
        let baseNumOfBeers = Math.floor(favoriteArr.length / 3);
        let leftover = favoriteArr.length % 3;
        let favoriteList1;
        let favoriteList2;
        let favoriteList3;
        for (let i = 0; i < 3; i++) {
          if (i === 0) {
            if (leftover === 2) {
              favoriteList1 = favoriteArr.slice(0, baseNumOfBeers + 1);
            }
            else {
              favoriteList1 = favoriteArr.slice(0, baseNumOfBeers);
            }
          }
          else if (i === 1) {
            if (leftover === 1) {
              favoriteList2 = favoriteArr.slice(baseNumOfBeers, baseNumOfBeers * 2 + 1);
            }
            else if (leftover === 2) {
              favoriteList2 = favoriteArr.slice(baseNumOfBeers + 1, baseNumOfBeers * 2 + 1);
            }
            else {
              favoriteList2 = favoriteArr.slice(baseNumOfBeers, baseNumOfBeers * 2);
            }
          }
          else {
            if (leftover === 1 || leftover === 2) {
              favoriteList3 = favoriteArr.slice(baseNumOfBeers * 2 + 1);
            }
            else {
              favoriteList3 = favoriteArr.slice(baseNumOfBeers * 2);
            }
          }
        }
        return [favoriteList1, favoriteList2, favoriteList3];
      }



    render() {
        return (
            <div className="d-flex justify-content-center align-items-center favorite-container w-100">
                {this.favoriteContainer()}
            </div>
        );
    }
}


export default Favorites;
