import React from 'react';
import ReactDOM from 'react-dom';
import './css/App.css';
import Navbar from './Navbar';
import Beers from './Beers';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {beersJson: null, 
                  jsonIndex: 0,
                  searchTerm: null,
                  page: 1,
                  clearBeers: false};
  }

  getAndDisplayBeers = (searchTerm) => {
    fetch(`https://api.punkapi.com/v2/beers?page=1&per_page=80&beer_name=${searchTerm}`)
    .then((response) => {
      return response.json();
    })
    .then((json) => {
      console.log(json);
      this.setState({beersJson: json,
                    searchTerm: searchTerm,
                    clearBeers: true,
                    page: 1});
      this.displayBeers();
    })
    .catch((error) => {
      console.error(error);
    })
  }

  displayBeers = () => {
    ReactDOM.render(<Beers json={this.state.json}  jsonIndex={this.state.jsonIndex} updateApp={this.setState} getAndDisplayBeers={this.getAndDisplayBeers} page={this.state.page}></Beers>, document.getElementById("beers"))
  }

  render() { 
    return (
      <div>
        <Navbar getAndDisplayBeers={this.getAndDisplayBeers}></Navbar>
        <div className="container" id="beers"></div>
      </div>
    );
  }
}

export default App;
