import React from 'react';
import { Route, Switch, Router } from 'react-router-dom';
import Results from './Results';
import Favorites from './Favorites';
import Navbar from './Navbar';

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            favoriteList: [],
            json: null, 
            jsonIndex: 0,
            searchTerm: null,
            page: 1,
            beerList: [],
            displayButton: false,
            haventSearched: true
        }
    }

    getAndDisplayRandomBeers = (beerCount, beerList) => {
        if (beerCount > 0) {
            fetch('https://api.punkapi.com/v2/beers/random')
            .then((response) => {
                return response.json();
            })
            .then((json) => {
                let beerFound = beerList.find((beer) => {
                    return beer.id === json.id;
                });
                if (!beerFound) {
                    console.log('found a new one');
                    let newBeerList = beerList;
                    newBeerList.push(json);
                    this.getAndDisplayRandomBeers(beerCount - 1, newBeerList);
                }
                else {
                    console.log('already there')
                    this.getAndDisplayRandomBeers(beerCount, beerList);
                }
            })
            .catch((error) => {
                console.error(error);
            });
        }
        else {
            this.setState({beerList: beerList}, () => {
                console.log(this.state.beerList);
            });
        }
    }

    getAndDisplayBeers = (searchTerm) => {
        fetch(`https://api.punkapi.com/v2/beers?page=${this.state.page}&per_page=80&beer_name=${searchTerm}`)
        .then((response) => {
          return response.json();
        })
        .then((json) => {
          if (this.state.json !== null) {
            if (JSON.stringify(this.state.json) === JSON.stringify(json)) {
              return;
            }
          }
          if (searchTerm === this.state.searchTerm) {
            this.setState({json: json,
                           jsonIndex: 0}, () => {
                              this.displayBeers();
                            });
          }
          else {
            this.setState({json: json,
                           jsonIndex: 0,
                           searchTerm: searchTerm,
                           page: 1,
                           beerList: null,
                           haventSearched: false}, () => {
                              this.displayBeers();
                          });
          }
        })
        .catch((error) => {
          console.error(error);
        })
    }

    displayBeers = () => {
        let numBeers = this.state.json.length - this.state.jsonIndex;
        if (numBeers >= 6) {
            this.makeBeerListThenDisplay(6);
        }
        else if (numBeers !== 0) {
            this.makeBeerListThenDisplay(numBeers);
        }
        else if (this.state.json.length === 80) {
            this.setState({page: this.props.app.state.page + 1}, () => {
            this.getAndDisplayBeers(this.state.searchTerm);
            });
        }
        else {
            this.setState({page: this.props.app.state.page + 1}, () => {
            return;
            });
        }
    }

    makeBeerListThenDisplay = (numBeers) => {
        let beerList = [];
        if (this.state.beerList != null) { 
          for (let i = 0; i < this.state.beerList.length; i++) {
              beerList.push(this.state.beerList[i]);
          }
        }
        let jsonIndex = this.state.jsonIndex;
        for (let i = 0; i < numBeers; i++) {
            beerList.push({id: this.state.json[jsonIndex].id,
                           img: this.state.json[jsonIndex].image_url,
                           name: this.state.json[jsonIndex].name,
                           description: this.state.json[jsonIndex].description});
            jsonIndex++;               
        }
        this.setState({beerList: beerList, jsonIndex: jsonIndex}, () => {
            let trueOrFalse;
            if (this.state.jsonIndex !== this.state.json.length) {
                trueOrFalse = true;
            }
            else {
                trueOrFalse = false;
            }
            this.setState({displayButton: trueOrFalse}, () => {
                this.props.history.push('/');
            });
        });
    }

    addToFavorites = (beer) => {
        let beerFound = this.state.favoriteList.find((beer1) => {
            return beer1.id === beer.id;
        });
        if (!beerFound) {
            let newFavoriteList = this.state.favoriteList;
            newFavoriteList.push(beer);
            this.setState({
                favoriteList: newFavoriteList
            });
        }
    }

    resetAndRedirect = (route) => {
        this.setState({json: null, 
                       jsonIndex: 0,
                       searchTerm: null,
                       page: 1,
                       beerList: [],
                       displayButton: false,
                       haventSearched: true}, () => {
                           this.props.history.push(`/${route}`)
                       });
    }

    removeFromFavorites = (beer) => {
        let beerIndex = this.state.favoriteList.findIndex((beer1) => {
            return beer1.id === beer.id;
        });
        let newFavoriteList = this.state.favoriteList;
        newFavoriteList.splice(beerIndex, 1);
        this.setState({favoriteList: newFavoriteList}, () => console.log(this.state.favoriteList));
    }

    componentDidMount() {
        if (this.props.history.location.pathname === '/') {
            if (this.state.haventSearched) {
                let beerList = [];
                this.getAndDisplayRandomBeers(6, beerList);
            }
        }
    }

    render() {
        return (
            <Router history={this.props.history}>
                <Switch>
                    <Route 
                        exact path="/" 
                        render={(props) => 
                            <div>
                                <Navbar {...props} getAndDisplayBeers={this.getAndDisplayBeers} favoriteNum={this.state.favoriteList.length} resetAndRedirect={this.resetAndRedirect}></Navbar>
                                <Results favoriteList={this.state.favoriteList} addToFavorites={this.addToFavorites} beerList={this.state.beerList} displayButton={this.state.displayButton} displayBeers={this.displayBeers}></Results>
                            </div>}>
                    </Route>
                    <Route 
                        path="/favorites" 
                        render={(props) =>
                            <div>
                                <Navbar {...props} getAndDisplayBeers={this.getAndDisplayBeers} favoriteNum={this.state.favoriteList.length} resetAndRedirect={this.resetAndRedirect}></Navbar>
                                <Favorites favoriteList={this.state.favoriteList} removeFromFavorites={this.removeFromFavorites}></Favorites>
                            </div>}>
                    </Route>
                </Switch>
            </Router>
        );
    }
}

export default App;
