import React from 'react';
import ReactDOM from 'react-dom';
import './css/App.css';
import Navbar from './Navbar';
import Beers from './Beers';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {json: null, 
                  jsonIndex: 0,
                  searchTerm: null,
                  page: 1,
                  clearBeers: false,
                  beerList1: null,
                  beerList2: null};
  }

  getAndDisplayBeers = (searchTerm) => {
    fetch(`https://api.punkapi.com/v2/beers?page=${this.state.page}&per_page=80&beer_name=${searchTerm}`)
    .then((response) => {
      return response.json();
    })
    .then((json) => {
      this.setState({json: json,
                    searchTerm: searchTerm,
                    clearBeers: true,
                    page: 1,
                    beerList1: null,
                    beerList2: null});
      this.prepareToDisplayBeers();
    })
    .catch((error) => {
      console.error(error);
    })
  }

  prepareToDisplayBeers = () => {
    for (let i = 1; i < 3; i++) {
      let listNum = i;
      let numBeers = this.state.json.length - this.state.jsonIndex;
      if (numBeers >= 1) {
        this.makeBeerList(listNum, numBeers);
      }
      else if (this.props.app.json.length === 80) {
        this.props.app.setState({page: this.props.app.state.page + 1});
        this.props.app.getAndDisplayBeers(this.props.app.state.searchTerm);
      }
      else {
        return;
      }
    }
  }

  makeBeerList = (listNum, numBeers) => {
    let beerList = [];
    for (let i = 0; i < numBeers; i++) {
        beerList.push({img: this.props.app.state.json[this.props.app.state.jsonIndex].image_url,
                       name: this.props.app.state.json[this.props.app.state.jsonIndex].name,
                       description: this.props.app.state.json[this.props.app.state.jsonIndex].description});
        this.props.app.setState({jsonIndex: this.props.app.state.jsonIndex + 1});
    }
    if (listNum === 1) {
      this.setState({beerList1: beerList});
    }
    else if (listNum === 2) {
      this.setState({beerList2: beerList});
    }
  }

  displayBeers = () => {
    ReactDOM.render(<Beers app={this}></Beers>, document.getElementById('beers'));
  }

  render() { 
    return (
      <div>
        <Navbar getAndDisplayBeers={this.getAndDisplayBeers}></Navbar>
        <div className="container" id="beers"></div>
      </div>
    );
  }
}

export default App;
