import React from 'react';
import './css/Navbar.css';

class Navbar extends React.Component {
  constructor(props) {
    super(props);
  }

  getAndDisplayBeers = () => {
    let searchValue = document.forms['search-form']['search'].value;
    document.forms['search-form']['search'].value = null;
    this.props.getAndDisplayBeers(searchValue);
  }

  submit = (e) => {
    e.preventDefault();
    this.getAndDisplayBeers();
  }

  redirect = (e) => {
    if (e.target.id === 'logo-container') {
      this.props.resetAndRedirect('');
    }
    else if (e.target.id === 'favorites-btn') {
      this.props.resetAndRedirect('favorites');
    }
  }

  render() {
    return (
      <nav className="navbar navbar-light bg-light
      justify-content-between align-items-center bg-secondary fixed-top flex-wrap">
        <div id="logo-and-search-container" className="d-flex w-75 align-items-center">
          <h1 id="logo-container" onClick={this.redirect}>Beer</h1>
          <form className="d-flex flex-grow-1 ml-5" name="search-form" id="search-form" onSubmit={this.submit}>
            <input id="search" type="text" className="form-control rounded-left" placeholder="Search for beer..." name="search"></input>
            <button className="btn btn-primary rounded-right" type="submit" id="search-btn">Search</button>
          </form>
        </div>
        <button type="button" className="btn btn-primary" onClick={this.redirect} id="favorites-btn">Favorites <span className="badge badge-light">{this.props.favoriteNum}</span></button>
      </nav>
    ); 
  }
}

export default Navbar;
