import React from 'react';
import './css/Login.css'

class Login extends React.Component {
    formHandler = () => {
        e.preventDefault();
        let loginForm = document.forms['login-form'];
        let userInfo = {firstname: signupForm['firstname'].value,
                        lastname: signupForm['lastname'].value,
                        email: signupForm['email'].value,
                        username: signupForm['username'].value,
                        password: signupForm['password1'].value};
        this.signUp('http://localhost:5000/signup', userInfo)

    }

    login = (url = '', data = {}) => {
        fetch(url, {
            method: 'POST',
            body: JSON.stringify(data),
            headers: {
                'Content-Type': 'application/json'
            }
        })
        .then((response) => response.json())
        .then((json) => {
            if (json.statusCode === 1) {
                this.props.history.push('/login');
            }
            else {
                let warningText = json.message;
                ReactDOM.render(<div className="d-flex justify-content-center align-items center alert alert-warning w-100" role="alert">
                {warningText}</div>, document.getElementById('warning'));
                return;
            }
        })
        .catch((error) => {
            console.error(error.stack);
            return;
        });
    };

    render() {
        return (
            <div className="w-100 h-100 d-flex flex-column justify-content-center align-items-center">
                <div id="warning" className="w-50"></div>
                <form className="d-flex flex-column w-50 mt-2 mb-4" onSubmit={this.formHandler} name="login-form">
                    <input type="text" className="form-control mb-2" placeholder="Username" name="username"></input>
                    <input type="text" className="form-control mb-3" placeholder="Password" name="password"></input>
                    <button type="submit" className="btn btn-outline-primary w-25 align-self-center">Log in</button>
                </form>
                <div class="alert alert-primary" role="alert">
                    New user? Sign up <a href="/signup" class="alert-link">here</a>.
                </div>
            </div>
        );
    }
}



export default Login;
