import React from 'react';
import './css/Main.css';

class Main extends React.Component {
    redirect = (e) => {
        if (e.target.innerHTML === 'Log In')
            this.props.history.push('/login');
        else if (e.target.innerHTML === 'Sign Up') {
            this.props.history.push('/signup');
        }
    }

    render() {
        return (
            <div id="main" className="d-flex flex-column justify-content-center align-items-center bg-light">
                <button className="btn-lg btn-primary mb-4" onClick={this.redirect}>Log In</button>
                <button className="btn-lg btn-primary" onClick={this.redirect}>Sign Up</button>
            </div>
        );
    }
}



export default Main;
