import React from 'react';
import './css/Navbar.css';

class Navbar extends React.Component {

  getAndDisplayBeers = () => {
    let searchValue = document.forms['search-form']['search'].value
    this.props.getAndDisplayBeers(searchValue);
  }

  submit = (e) => {
    e.preventDefault();
    this.getAndDisplayBeers();
  }

  render() {
    return (
      <nav className="navbar navbar-light bg-light
      justify-content-center align-items-center bg-secondary fixed-top">
        <form className="w-75 h-25 border border-secondary" name="search-form" id="search-form" onSubmit={this.submit}>
          <input type="text" className="form-control" placeholder="Search for beer..." name="search"></input>
          <button className="btn btn-primary" type="submit">Search</button>
        </form>
      </nav>
    ); 
  }
}

export default Navbar;
