import React from 'react';
import ReactDOM from 'react-dom';
import './css/App.css';
import Navbar from './Navbar';
import Beers from './Beers';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {json: null, 
                  jsonIndex: 0,
                  searchTerm: null,
                  page: 1,
                  clearBeers: false,
                  beerList: null};
  }

  getAndDisplayBeers = (searchTerm) => {
    fetch(`https://api.punkapi.com/v2/beers?page=${this.state.page}&per_page=80&beer_name=${searchTerm}`)
    .then((response) => {
      return response.json();
    })
    .then((json) => {
      this.setState({json: json,
                    searchTerm: searchTerm,
                    clearBeers: true,
                    page: 1});
      this.prepareToDisplayBeers();
      this.displayBeers();
    })
    .catch((error) => {
      console.error(error);
    })
  }

  prepareToDisplayBeers = () => {
      let numBeers = this.state.json.length - this.state.jsonIndex;
      if (numBeers >= 6) {
        this.makeBeerList(6);
      }
      else if (numBeers !== 0) {
        this.makeBeerList(numBeers);
      }
      else if (this.state.json.length === 80) {
        this.setState({page: this.props.app.state.page + 1});
        this.getAndDisplayBeers(this.state.searchTerm);
      }
      else {
        return;
      }
    }

  makeBeerList = (numBeers) => {
    let beerList = [];
    if (this.state.beerList !== null) {
      for (let i = 0; i < this.state.beerList.length; i++) {
        beerList.push(this.state.beerList[i]);
      }
    }
    for (let i = 0; i < numBeers; i++) {
        beerList.push({img: this.state.json[this.state.jsonIndex].image_url,
                       name: this.state.json[this.state.jsonIndex].name,
                       description: this.state.json[this.state.jsonIndex].description});
        this.setState({jsonIndex: this.state.jsonIndex + 1});
    }
    this.setState({beerList: beerList});
  }

  displayBeers = () => {
    ReactDOM.render(<Beers beerList={this.state.beerList}></Beers>, document.getElementById('beers'));
    this.displayButton();
  }

  displayButton = () => {
    ReactDOM.render(<button className="btn btn-primary w-50" onClick={this.prepareToDisplayBeers}>Load More</button>, document.getElementById('load-more-container'));
  }

  render() { 
    return (
      <div>
        <Navbar getAndDisplayBeers={this.getAndDisplayBeers}></Navbar>
        <div id="beers"></div>
        <div className="w-100 m-2 d-flex justify-content-center align-items-center" id="load-more-container"></div>
      </div>
    );
  }
}

export default App;
