import React from 'react';
import ReactDOM from 'react-dom';
import { Route, Link, Switch, BrowserRouter as Router } from 'react-router-dom';
import './index.css';
import Home from './components/Home';
import Favorites from './components/Favorites';
import * as serviceWorker from './serviceWorker';
import 'bootstrap/dist/css/bootstrap.css';

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            favoriteList: []
        }
    }

    addToFavorites = (beer) => {
        let newFavoriteList = this.state.favoriteList;
        newFavoriteList.push(beer);
        this.setState({
            favoriteList: newFavoriteList
        });
    }

    getFavoriteList = () => {
        return this.state.favoriteList;
    }

    render() {
        return (
            <Router>
                <Switch>
                    <Route 
                        exact path="/" 
                        render={() => <Home favoriteList={this.state.favoriteList}
                        addToFavorites={this.addToFavorites}></Home>}>        
                    </Route>
                    <Route 
                        path="/favorites" 
                        render={() => <Favorites favoriteList={this.state.favoriteList} getFavoriteList={this.getFavoriteList}></Favorites>}
                    ></Route>
                </Switch>
            </Router>
        );
    }
}

ReactDOM.render(<App></App>, document.getElementById('root'));

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
