import React from 'react';
import './css/Favorites.css';
import Beer from './Beer';

class Favorites extends React.Component {
    beerContainer = () => {
        if (this.props.beerList !== null) {
          if (this.props.beerList.length < 3) {
            let html = (<div className="d-flex justify-content-center w-100 less-than-three-container">
                          {this.props.beerList.map(beer => <Beer key={beer.id} beerKey={beer.id} attributes={beer} removeFromFavorites={this.props.removeFromFavorites}></Beer>)}
                        </div>)
            return html;
          }
          else {
            let beerLists = this.createAndRenderBeerLists();
            let html = (<div className="d-flex justify-content-center flex-wrap w-100 greater-than-three-container">
                          <div id="beer-column-1" className="beer-column mr-2">{beerLists[0]}</div>
                          <div id="beer-column-2" className="beer-column">{beerLists[1]}</div>
                          <div id="beer-column-3" className="beer-column ml-2">{beerLists[2]}</div>
                        </div>);
            return html;
          }
        }
      }


    createAndRenderBeerLists = () => {
        let beerArr = this.props.favoritesList.map(beer => <Beer key={beer.id} beerKey={beer.id} attributes={beer} removeFromFavorites={this.props.removeFromFavorites}></Beer>);
        console.log(beerArr.length);
        let baseNumOfBeers = Math.floor(beerArr.length / 3);
        let leftover = beerArr.length % 3;
        let beerList1;
        let beerList2;
        let beerList3;
        for (let i = 0; i < 3; i++) {
          if (i === 0) {
            if (leftover === 2) {
              beerList1 = beerArr.slice(0, baseNumOfBeers + 1);
            }
            else {
              beerList1 = beerArr.slice(0, baseNumOfBeers);
            }
          }
          else if (i === 1) {
            if (leftover === 1) {
              beerList2 = beerArr.slice(baseNumOfBeers, baseNumOfBeers * 2 + 1);
            }
            else if (leftover === 2) {
              beerList2 = beerArr.slice(baseNumOfBeers + 1, baseNumOfBeers * 2 + 1);
            }
            else {
              beerList2 = beerArr.slice(baseNumOfBeers, baseNumOfBeers * 2);
            }
          }
          else {
            if (leftover === 1 || leftover === 2) {
              beerList3 = beerArr.slice(baseNumOfBeers * 2 + 1);
            }
            else {
              beerList3 = beerArr.slice(baseNumOfBeers * 2);
            }
          }
        }
        return [beerList1, beerList2, beerList3];
      }



    render() {
        return (
            <div className="d-flex justify-content-center align-items-center favorites-container w-100">
                {this.beerContainer()}
            </div>
        );
    }
}


export default Favorites;
