import React from 'react';
import './css/Signup.css';
import ReactDOM from 'react-dom';

class Signup extends React.Component {
    redirect = (e) => {
    }

    formHandler = (e) => {
        e.preventDefault();
        let signupForm = document.forms['signup-form'];
        if (signupForm['password1'].value !== signupForm['password2'].value) {
            alert('Passwords do not match.');
            return;
        }
        let userInfo = {firstname: signupForm['firstname'].value,
                        lastname: signupForm['lastname'].value,
                        email: signupForm['email'].value,
                        password: signupForm['password1'].value};
        this.signUp('http://localhost:5000/signup', userInfo)
    }

    signUp = (url = '', data = {}) => {
        fetch(url, {
            method: 'POST',
            body: JSON.stringify(data),
            headers: {
                'Content-Type': 'application/json'
            }
        })
        .then((response) => response.json())
        .then((json) => {
            if (json.statusCode === 1) {
                this.props.history.push('/login');
            }
            else {
                let warningText = json.message;
                ReactDOM.render(<div className="d-flex justify-content-center align-items center alert alert-warning w-100" role="alert">
                {warningText}</div>, document.getElementById('warning'));
                return;
            }
        })
        .catch((error) => {
            console.error(error.stack);
            return;
        });
    };

    render() {
        return (
            <div id="signup-container-1" className="w-100 d-flex justify-content-center">
                <div id="signup-container-2" className="d-flex flex-column justify-content-center align-items-center border border-light rounded shadow">
                    <div id="warning" className="mb-2 sign-up"></div>
                    <form className="d-flex justify-content-center align-items-center flex-column w-100" onSubmit={this.formHandler} name="signup-form">
                        <div className="form-group mb-2 d-flex justify-content-center align-items-center sign-up">
                            <input type="text" className="form-control mr-3" placeholder="First name" name="firstname"></input>
                            <input type="text" className="form-control" placeholder="Last name" name="lastname"></input>
                        </div>
                        <div className="form-group mb-2 sign-up">
                            <input type="email" className="form-control" placeholder="Email" name="email"></input>
                        </div>
                        <div className="form-group sign-up">
                            <input type="password" className="form-control mb-2" placeholder="Password" name="password1"></input>
                            <input type="password" className="form-control" placeholder="Confirm password" name="password2"></input>
                        </div>
                        <button type="submit" id="sign-up-btn" className="btn btn-outline-primary align-self-center">Sign up</button>
                    </form>
                </div>
            </div>
        );
    }
}

export default Signup;
