import React from 'react';
import './css/Beer.css';
import $ from 'jquery';

$('.click').click(function() {
	if ($('span').hasClass("fa-star")) {
			$('.click').removeClass('active')
		setTimeout(function() {
			$('.click').removeClass('active-2')
		}, 30)
			$('.click').removeClass('active-3')
		setTimeout(function() {
			$('span').removeClass('fa-star')
			$('span').addClass('fa-star-o')
		}, 15)
	} else {
		$('.click').addClass('active')
		$('.click').addClass('active-2')
		setTimeout(function() {
			$('span').addClass('fa-star')
			$('span').removeClass('fa-star-o')
		}, 150)
		setTimeout(function() {
			$('.click').addClass('active-3')
		}, 150)
		$('.info').addClass('info-tog')
		setTimeout(function(){
			$('.info').removeClass('info-tog')
		},1000)
	}
})

class Beer extends React.Component {
  trimDescription = () => {
    let description = this.props.attributes.description.slice(0, 200);
    if (description.charAt(description.length - 1) === ' ') {
      description = description.slice(0, description.length - 1);
    }
    if (description.length === this.props.attributes.description.length) {
      return description;
    }
    else {
      return description + '...';
    }
  }

  render() {
    return (<div className="beer-card border border-secondary m-2">
              <div className="favorite-container">
                <div className="click">
                  <span className="fa fa-star-o"></span>
                  <div className="ring"></div>
                  <div className="ring2"></div>
                  <p className="info">Added to favourites!</p>
                </div>
              </div>
              <div className="img-and-description-container">
                <div className="beer-img-container">
                    <img className="beer-img" src={this.props.attributes.img} alt="Beer"></img>
                </div>
                <div className="description-container">
                  <h5 className="beer-name">{this.props.attributes.name}</h5>
                  <div className="beer-description">{this.trimDescription()}</div>
                </div>
              </div>
            </div>);
  }
}

export default Beer;
