import React from 'react';
import './css/Navbar.css';

class Navbar extends React.Component {

  getAndDisplayBeers = () => {
    let searchValue = document.forms['search-form']['search'].value
    this.props.getAndDisplayBeers(searchValue);
  }

  render() {
    return (
      <nav className="navbar navbar-light bg-light
      justify-content-center align-items-center bg-secondary fixed-top">
        <form className="form-inline justify-content-center align-items-center w-75" name="search-form" id="search-form" onsubmit="return false">
                <div className="input-group w-100">
                  <input type="text" className="form-control" placeholder="Search for beer..." name="search"/>
                  <div className="input-group-append">
                    <button className="btn btn-primary" type="button">Search</button>
                  </div>
              </div>
        </form>
      </nav>
    ); 
  }
}

export default Navbar;
