import React from 'react';
import BeerRow from './BeerRow';
import './css/Beers.css';

class Beers extends React.Component {
  constructor(props) {
    super(props);
    this.state = {beerList1: null,
                  beerList2: null};
    this.prepareToFillContainer();
  }

  prepareToFillContainer = () => {
    for (let i = 1; i < 3; i++) {
      let listNum = i;
      let numBeers = this.props.json.length - this.props.jsonIndex;
      if (numBeers >= 1) {
        this.makeBeerList(listNum, numBeers);
      }
      else if (this.props.json.length === 80) {
        this.props.updateApp({page: this.props.page + 1});
        this.props.getAndDisplayBeers();
      }
      else {
        return;
      }
    }
  }

  makeBeerList = (listNum, numBeers) => {
    let beerList = [];
    for (let i = 0; i < numBeers; i++) {
        beerList.push({img: this.props.json[this.props.jsonIndex].image_url,
                       name: this.props.json[this.props.jsonIndex].name,
                       description: this.props.json[this.props.jsonIndex].description});
        this.props.updateApp({jsonIndex: this.props.jsonIndex + 1});
    }
    if (listNum === 1) {
      this.setState({beerList1: beerList});
    }
    else if (listNum === 2) {
      this.setState({beerList2: beerList});
    }
  }

  render() {
    if (this.state.beerList1 != null && this.state.beerList2 != null) {
      return (
        <div className="container">
          <BeerRow beerList={this.state.beerList1}></BeerRow>
          <BeerRow beerList={this.state.beerList2}></BeerRow>
        </div>
      ); 
    }
    else if (this.state.beerList1 != null) {
      return (
        <div className="container">
          <BeerRow beerList={this.state.beerList1}></BeerRow>
        </div>
      ); 
    }
    else if (this.state.beerList != null) {
      return (
        <div className="container">
          <BeerRow beerList={this.state.beerList2}></BeerRow>
        </div>
      ); 
    }
  }
}

export default Beers;
