import React from 'react';
import Beers from './Beers';
import './css/Beers.css';

class BeersContainer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {beerList1: null,
                  beerList2: null};
    prepareToFillContainer();
  }

  prepareToFillContainer = () => {
    let numBeers = this.props.json.length - this.props.index + 1;
    if (numBeers >= 3) {
        makeBeerList(1, numBeers);
    }
    else if (this.props.json.length = 80) {
      this.props.updateApp({page: this.props.page++});
      this.props.getAndDisplayBeers();
    }
    else {
      return;
    }

    numBeers = this.props.json.length - this.props.index + 1;
    if (numBeers >= 3) {
        makeBeerList(2, numBeers);
    }
    else if (this.props.json.length = 80) {
      this.props.updateApp({page: this.props.page++});
      this.props.getAndDisplayBeers();
    }
    else {
      return;
    }
  }

  makeBeerList = (listNum, numBeers) => {
    let beerList = [];
    for (let i = 0; i < numBeers; i++) {
        beerList.push({img: this.props.json[jsonIndex].image_url,
                       name: this.props.json[jsonIndex].name,
                       description: this.props.json[jsonIndex].description});
        this.props.updateApp({jsonIndex: this.props.jsonIndex++});
    }
    if (listNum === 1) {
      this.setState({beerList1: beerList});
    }
    else if (listNum === 2) {
      this.setState({beerList2: beerList});
    }
  }

  render() {
    return (
      <div className="container">
        <Beers></Beers>
        <Beers></Beers>
      </div>
    ); 
  }
}

export default BeersContainer;
