import React from 'react';
import './css/BeerCol.css';

class BeerCol extends React.Component {
  trimDescription() {
    let description = this.props.attributes.description.slice(0, 150);
    if (description.charAt(description.length - 1) === ' ') {
      description = description.slice(0, description.length - 1);
    }
    return description + '...';
  }

  render() {
    return (
      <div className="col m-2 border border-secondary shadow p-3 mb-5 bg-white rounded">
          <div class="container-fluid border border-primary">
            <div className="row justify-content-end align-items-end border border-secondary">
              <div className="col">*</div>
            </div>
            <div className="row justify-content-between align-items-center">
                <div className="col-5 border border-secondary">
                  <img className="img-fluid" src={this.props.attributes.img} alt="Beer"></img>
                </div>
                <div className="col-7 border border-primary">
                    <h2>{this.props.attributes.name}</h2>
                    <div>{this.trimDescription()}</div>
                </div>
            </div>
          </div>
      </div>
    ); 
  }
}

export default BeerCol;
