import React from 'react';
import './css/Results.css';
import Beer from './Beer';

class Results extends React.Component {
  displayButton = () => {
    if (this.props.displayButton === true) {
      return(<button className="btn btn-primary w-50" onClick={this.props.displayBeers}>Load More</button>);
    }
  }

  componentDidUpdate() {
    let beerArr = this.props.beerList.map(beer => <Beer key={beer.id} beerKey={beer.id} attributes={beer} addToFavorites={this.props.addToFavorites}></Beer>)
    let numForAtLeastTwoCols = Math.floor(beerArr.length / 3);
    let beerList1;
    let beerList2;
    let beerList3;
    for (let i = 0; i < 3; i++) {
      if (num === 0) {
        beerList1 = beerArr.slice(0, numForAtLeastTwoCols);
        ReactDOM.render(beerList1, document.getElementById(beer-column-1))
      }
      else if (num === 1) {
        beerList2 = beerArr.slice(numForAtLeastTwoCols, numForAtLeastTwoCols);
        ReactDOM.render(beerList2, document.getElementById(beer-column-2))
      }
      else {
        beerList3 = beerArr.slice(numForAtLeastTwoCols * 2);
        ReactDOM.render(beerList3, document.getElementById(beer-column-3))
      }
    }

  }

  render() {
    return (
      <div className="d-flex justify-content-center flex-wrap w-100 h-100"> 
        <div id="beer-column-1" className="beer-column">{this.createBeerList(1)}</div>
        <div id="beer-column-2" className="beer-column">{this.createBeerList(2)}</div>
        <div id="beer-column-3" className="beer-column">{this.createBeerList(3)}</div>
        <div className="w-100 m-2 d-flex justify-content-center align-items-center" id="load-more-container">                    {this.displayButton()}
        </div>
      </div>
    );
  }
}

export default Results;
