import React from 'react';
import ReactDOM from 'react-dom';
import './css/Login.css'

class Login extends React.Component {
    formHandler = (e) => {
        e.preventDefault();
        let loginForm = document.forms['login-form'];
        let userInfo = {email: loginForm['email'].value,
                        password: loginForm['password'].value};
        this.login('http://localhost:5000/login', userInfo)

    }

    login = async(url = '', data = {}) => {
        fetch(url, {
            method: 'POST',
            body: JSON.stringify(data),
            headers: {
                'Content-Type': 'application/json'
            }
        })
        .then((response) => response.json())
        .then((json) => {
            if (json.statusCode === 1 && json.beerIDs !== undefined) {
                this.props.updateFavoritesFromLogin(json.beerIDs);
                this.props.resetAndRedirect('');
            }
            else if (json.statusCode === 1) {
                this.props.resetAndRedirect('')
            } 
            else {
                let warningText = json.message;
                ReactDOM.render(<div className="d-flex justify-content-center align-items center alert alert-warning w-100" role="alert">
                {warningText}</div>, document.getElementById('warning'));
                return;
            }
        })
        .catch((error) => {
            console.error(error.stack);
            return;
        });
    };

    render() {
        return (
            <div className="w-100 h-100 d-flex flex-column justify-content-center align-items-center">
                <div id="warning" className="w-50"></div>
                <form className="d-flex flex-column w-50 mt-2 mb-4" onSubmit={this.formHandler} name="login-form">
                    <input type="text" className="form-control mb-2" placeholder="Email" name="email"></input>
                    <input type="password" className="form-control mb-3" placeholder="Password" name="password"></input>
                    <button type="submit" className="btn btn-outline-primary w-25 align-self-center">Log in</button>
                </form>
                <div className="alert alert-primary" role="alert">
                    New user? Sign up <a href="/signup" className="alert-link">here</a>.
                </div>
            </div>
        );
    }
}



export default Login;
