import React from 'react';
import './css/BeerCol.css';

class BeerCol extends React.Component {
  trimDescription() {
    let description = this.props.attributes.description.slice(0, 150);
    return description + '...';
  }

  render() {
    return (
      <div className="col-sm h-15 m-2 border border-secondary shadow p-3 mb-5 bg-white rounded overflow-hidden">
          <div className="d-flex justify-content-end align-items-center">*</div>
          <div className="d-flex justify-content-between align-items-center">
              <div className="w-35">
                <img className="img-fluid" src={this.props.attributes.img} alt="Beer"></img>
              </div>
              <div className="w-55">
                  <h2>{this.props.attributes.name}</h2>
                  <div>{this.trimDescription()}</div>
              </div>
          </div>
      </div>
    ); 
  }
}

export default BeerCol;
