import React from 'react';
import './css/App.css';
import Navbar from './Navbar';
import Beers from './Beers';

class App extends React.Component() {
  constructor(props) {
    super(props);
    this.state = {beersJson: null, 
                  jsonPosition: 0,
                  searchTerm: null,
                  offset: 0,
                  clearBeers: false};
  }

  getAndDisplayBeers(searchValue) {
    fetch(`https://api.punkapi.com/v2/beers?page=0&per_page=100&beer_name=${searchValue}`)
    .then((response) => {
      return response.json();
    })
    .then((json) => {
      console.log(json);
    })
  }

  render() { 
    return (
      <div>
        <Navbar getAndDisplayResults={this.getAndDisplayResults}></Navbar>
      </div>
    );
  }
}

export default App;
