import React from 'react';
import './css/App.css';
import Navbar from './Navbar';
//import Beers from './Beers';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {beersJson: null, 
                  jsonIndex: 0,
                  searchTerm: null,
                  offset: 0,
                  clearBeers: false};
  }

  getAndDisplayBeers(searchValue) {
    fetch(`https://api.punkapi.com/v2/beers?page=1&per_page=80&beer_name=${searchValue}`)
    .then((response) => {
      return response.json();
    })
    .then((json) => {
      this.state.beersJson = json;
      this.state.searchTerm = null;
      this.state.clearBeers = true;
      this.displayBeers();
    })
    .catch((error) => {
      console.error(error);
    })
  }

  displayBeers = () => {
    ReactDOM.render(<Beers json={this.state.json}  index={this.state.jsonIndex}></Beers>)
  }

  render() { 
    return (
      <div>
        <Navbar getAndDisplayBeers={this.getAndDisplayBeers}></Navbar>
      </div>
    );
  }
}

export default App;
