import React from 'react';
import './css/Navbar.css';

class Navbar extends React.Component {

  getAndDisplayBeers = () => {
    let searchValue = document.forms['search-form']['search'].value
    this.props.getAndDisplayBeers(searchValue);
  }

  submit = (e) => {
    e.preventDefault();
    this.getAndDisplayBeers();
  }

  render() {
    return (
      <nav className="navbar navbar-light bg-light
      justify-content-around align-items-center bg-secondary fixed-top flex-wrap">
        <div id="logo-container">
          <img id="logo" src={this.props.logo} alt="Beer"></img>
        </div>
        <form className="d-flex w-75" name="search-form" id="search-form" onSubmit={this.submit}>
          <input type="text" className="form-control rounded-left" placeholder="Search for beer..." name="search"></input>
          <button className="btn btn-primary rounded-right" type="submit" id="search-btn">Search</button>
        </form>
        <button type="button" className="btn btn-primary m-1" id="favorites-counter">
          Favorites <span className="badge badge-light">0</span>
        </button>
      </nav>
    ); 
  }
}

export default Navbar;
