import React from 'react';
import './css/Signup.css';

class Signup extends React.Component {
    redirect = (e) => {
    }

    formHandler = (e) => {
        e.preventDefault();
        let signupForm = document.forms['signup-form'];
        if (signupForm['password1'].value !== signupForm['password2'].value) {
            alert('Passwords do not match.');
            return;
        }
        let userInfo = {firstname: signupForm['firstname'].value,
                        lastname: signupForm['lastname'].value,
                        email: signupForm['email'].value,
                        username: signupForm['username'].value,
                        password: signupForm['password1'].value};
        this.signUp('https://localhost:5000/signup', userInfo)
    }

    signUp = (url = '', data = {}) => {
        fetch(url, {
            method: 'PUT',
            body: JSON.stringify(data),
        })
        .then(response => console.log(response.json()))
        .catch(error => console.error(error));
    };

    render() {
        return (
            <div id="signup-container" className="d-flex justify-content-center align-items-center h-100 w-100">
                <form className="d-flex flex-column w-50" onSubmit={this.formHandler} name="signup-form">
                    <div className="form-group d-flex justify-content-center align-items-center">
                        <input type="text" className="form-control mr-3" placeholder="First name" name="firstname"></input>
                        <input type="text" className="form-control" placeholder="Last name" name="lastname"></input>
                    </div>
                    <div className="form-group">
                        <input type="email" className="form-control" placeholder="Email" name="email"></input>
                    </div>
                    <div className="form-group">
                        <input type="text" className="form-control" placeholder="Username" name="username"></input>
                    </div>
                    <div className="form-group">
                        <input type="password" className="form-control mb-2" placeholder="Password" name="password1"></input>
                        <input type="password" className="form-control" placeholder="Confirm password" name="password2"></input>
                    </div>
                    <button type="submit" className="btn btn-outline-primary w-25 align-self-center">Sign Up</button>
                </form>
            </div>
        );
    }
}

export default Signup;
