import React from 'react';
import ReactDOM from 'react-dom';
import './css/App.css';
import Navbar from './Navbar';
import Beer from './Beer';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {json: null, 
                  jsonIndex: 0,
                  searchTerm: null,
                  page: 1,
                  beerList: null};
  }

  getAndDisplayBeers = (searchTerm) => {
    fetch(`https://api.punkapi.com/v2/beers?page=${this.state.page}&per_page=80&beer_name=${searchTerm}`)
    .then((response) => {
      return response.json();
    })
    .then((json) => {
      if (this.state.json !== null) {
        if (JSON.stringify(this.state.json) === JSON.stringify(json)) {
          return;
        }
      }
      if (searchTerm === this.state.searchTerm) {
        this.setState({json: json,
                       jsonIndex: 0}, () => {
                          this.displayBeers();
                        });
      }
      else {
        this.setState({json: json,
                       jsonIndex: 0,
                       searchTerm: searchTerm,
                       page: 1,
                       beerList: null}, () => {
                          this.displayBeers();
                      });
      }
    })
    .catch((error) => {
      console.error(error);
    })
  }

  displayBeers = () => {
      let numBeers = this.state.json.length - this.state.jsonIndex;
      if (numBeers >= 6) {
        this.makeBeerListThenDisplay(6);
      }
      else if (numBeers !== 0) {
        this.makeBeerListThenDisplay(numBeers);
      }
      else if (this.state.json.length === 80) {
        this.setState({page: this.props.app.state.page + 1}, () => {
          this.getAndDisplayBeers(this.state.searchTerm);
        });
      }
      else {
        this.setState({page: this.props.app.state.page + 1}, () => {
          return;
        });
      }
    }

  makeBeerListThenDisplay = (numBeers) => {
    let beerList = [];
    if (this.state.beerList !== null) {
      for (let i = 0; i < this.state.beerList.length; i++) {
        beerList.push(this.state.beerList[i]);
      }
    }
    let jsonIndex = this.state.jsonIndex;
    for (let i = 0; i < numBeers; i++) {
        beerList.push({img: this.state.json[jsonIndex].image_url,
                       name: this.state.json[jsonIndex].name,
                       description: this.state.json[jsonIndex].description});
        jsonIndex++;               
    }
    this.setState({beerList: beerList, jsonIndex: jsonIndex}, () => {
      ReactDOM.render(this.state.beerList.map(beer => <Beer key={this.state.beerList.indexOf(beer)} attributes={beer}></Beer>), document.getElementById('beers'));
      if (this.state.jsonIndex !== this.state.json.length) {
        this.displayButton();
      }
      else {
        this.removeButton();
      }
    });
  }

  displayButton = () => {
    ReactDOM.render(<button className="btn btn-primary w-50" onClick={this.displayBeers}>Load More</button>, document.getElementById('load-more-container'));
  }

  getImage = () => {
    fetch('https://api.punkapi.com/v2/beers/random')
      .then((response) => {
        return response.json();
      })
      .then((json) => {
        if (json[0].image_url == null) {
          this.getImage();
        }
        else {
          return json[0].image_url;
        }
      })
      .catch((error) => {
        console.error(error);
      });
  }

  removeButton = () => {
    ReactDOM.render(null, document.getElementById('load-more-container'));
  }

  render() {
    let image = this.getImage();
    return (
      <div>
        <Navbar getAndDisplayBeers={this.getAndDisplayBeers} img={image}></Navbar>
        <div id="beers"></div>
        <div className="w-100 m-2 d-flex justify-content-center align-items-center" id="load-more-container"></div>
      </div>
    );
  }
}

export default App;
