import React from 'react';
import './css/BeerCol.css';

class BeerCol extends React.Component {
  trimDescription() {
    let description = this.props.attributes.description.slice(0, 150);
    if (description.charAt(description.length - 1) === ' ') {
      description = description.slice(0, description.length - 1);
    }
    return description + '...';
  }

  render() {
    return (<div className="beer-card">
              <div className="favorite-container">*</div>
              <div className="beer-img-container">
                  <img className="beer-img" src={this.props.attributes.img} alt="Beer"></img>
              </div>
            </div>);
